import { useState, useEffect, useRef } from "react";

function useOnScreen(threshold = 0.15) {
  const ref = useRef(null);
  const [vis, setVis] = useState(false);
  useEffect(() => {
    const el = ref.current;
    if (!el) return;
    const obs = new IntersectionObserver(([e]) => { if (e.isIntersecting) { setVis(true); obs.unobserve(el); } }, { threshold });
    obs.observe(el);
    return () => obs.disconnect();
  }, [threshold]);
  return [ref, vis];
}

function Reveal({ children, delay = 0, dir = "up", style = {} }) {
  const [ref, vis] = useOnScreen(0.1);
  const t = { up: "translateY(60px)", left: "translateX(60px)", right: "translateX(-60px)", none: "none" };
  return <div ref={ref} style={{ opacity: vis ? 1 : 0, transform: vis ? "none" : t[dir], transition: `all 0.9s cubic-bezier(0.16,1,0.3,1) ${delay}s`, ...style }}>{children}</div>;
}

